(() => {
  // ns-hugo-params:<stdin>
  var stdin_default = { defaultTheme: "auto" };

  // <stdin>
  (function() {
    const localStorage = window.localStorage;
    if (!localStorage) {
      return;
    }
    let isDark = false;
    const themeUsed = localStorage.getItem("theme");
    if (themeUsed) {
      isDark = themeUsed === "dark";
    } else {
      isDark = stdin_default.defaultTheme === "auto" ? window.matchMedia("(prefers-color-scheme: dark)").matches : stdin_default.defaultTheme === "dark";
    }
    isDark && (document.documentElement.dataset.theme = "dark");
  })();
})();
